import Vue from "vue";

import Item from '../interfaces/Item';

import ItemView from "./ItemView.vue";

import TQGlobals from "../classes/TQGlobals";

export default Vue.extend({

    props: {
        itemName: {
            type: String,
            required: true
        }
    },

    data: function () {
        return {
            item: null as Item | null
        };
    },

    watch: {
        itemName: function (newValue: string, oldValue: string) {
            this.setItemFromItemName(newValue);
        }
    },

    mounted: function () {
        TQGlobals.onInit(this.onInit);
    },

    methods: {
        onInit: function () {
            this.setItemFromItemName(this.itemName);
        },

        setItemFromItemName: function (name:string) {
            if (!name)
                this.item = null;
            else if (name.trim().length == 0)
                this.item = null;
            else if (TQGlobals.isInitComplete())
                this.item = TQGlobals.items.get(name);
        }
    },

    components: {
        ItemView
    },
});
