import Vue from "vue";

import Filter from "../interfaces/Filter";

import TQGlobals from "../classes/TQGlobals";


export default Vue.extend({

    data: function () {
        return {
            filter: null as Filter | null
        }
    },

    computed: {
        linkText: function () {
            let linkText = "Set Filters";

            if (this.filter != null) {
                let numActiveFilters = 0;

                if (this.filter.itemNames.length > 0)
                    numActiveFilters++;
                if (this.filter.playerName != null)
                    numActiveFilters++;
                if (this.filter.isBuying != null)
                    numActiveFilters++;
                if (this.filter.minPrice != null)
                    numActiveFilters++;
                if (this.filter.maxPrice != null)
                    numActiveFilters++;
                if (this.filter.minGoodPriceDeviation != null)
                    numActiveFilters++;
                if (this.filter.maxBadPriceDeviation != null)
                    numActiveFilters++;

                // STUB add more filters

                if (numActiveFilters > 0)
                    linkText += " (" + numActiveFilters.toString() + " active)";
            }

            return linkText + " >>";
        }
    },

    mounted: function () {
        TQGlobals.init(() => {
            this.filter = TQGlobals.filter;
        });
    }
});
